def Pascal_triangle(k):
    if k == 1:
        return [1]
    else:
        row = Pascal_triangle(k-1)
        row.append(0)
        new_row = []
        for i in range(len(row)-1):
            new_row.append(row[i]+row[i+1])
        new_row.insert(0,1)
    return new_row
print(Pascal_triangle(100))
